import React, { useState } from 'react';
import './Navbar.css';

const Navbar = ({ onNavigate, currentView, onLogout, user, products, onSearchSelect }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [showSuggestions, setShowSuggestions] = useState(false);

  const filteredProducts =
    searchTerm.trim().length === 0
      ? []
      : products.filter(p =>
          p.name.toLowerCase().includes(searchTerm.toLowerCase())
        );

  const handleChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);
    setShowSuggestions(value.trim().length > 0);
  };

  const handleSelectProduct = (product) => {
    setSearchTerm(product.name);
    setShowSuggestions(false);
    onSearchSelect(product); // parent will add to cart
  };

  const handleBlur = () => {
    // small timeout so click can register before closing
    setTimeout(() => setShowSuggestions(false), 150);
  };

  return (
    <header className="fb-navbar">
      {/* left part same as before */}

      <div className="fb-nav-center">
        <div className="fb-search-wrapper">
          <input
            className="fb-search-input"
            placeholder="Search for fruits, vegetables, juices..."
            value={searchTerm}
            onChange={handleChange}
            onFocus={() => setShowSuggestions(searchTerm.trim().length > 0)}
            onBlur={handleBlur}
          />
          {showSuggestions && filteredProducts.length > 0 && (
            <ul className="fb-search-suggestions">
              {filteredProducts.slice(0, 8).map((product) => (
                <li
                  key={product.id}
                  className="fb-search-suggestion-item"
                  onClick={() => handleSelectProduct(product)}
                >
                  {product.name}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* right part same as before */}
    </header>
  );
};

export default Navbar;
