import React from 'react';
import './Shop.css';

function Shop({
  products = [],
  loadingProducts,
  cartItems = [],
  wishlistItems = [],
  onSetCartQuantity,
  onAddToWishlist,
  onOpenProduct,
}) {
  const getCartQuantity = (productId) => {
    const item = cartItems.find((ci) => ci.product._id === productId);
    return item ? item.quantity : 0;
  };

  if (loadingProducts) {
    return (
      <div className="shop-loading">
        Loading products...
      </div>
    );
  }

  if (!products || products.length === 0) {
    return (
      <div className="shop-empty">
        <p>No products available.</p>
      </div>
    );
  }

  return (
    <div className="shop-page">
      <div className="shop-header">
        <h1 className="shop-title">Shop</h1>
        <p className="shop-subtitle">
          Fresh fruits and vegetables picked for you.
        </p>
      </div>

      <div className="shop-grid">
        {products.map((product) => {
          const qty = getCartQuantity(product._id);
          const isWished = wishlistItems.some(
            (item) => item._id === product._id
          );

          return (
            <article
              key={product._id}
              className="shop-product-card"
            >
              <div
                className="shop-product-image"
                onClick={() => onOpenProduct(product)}
              >
                <img
                  src={product.imageUrl}
                  alt={product.name}
                  className="shop-product-img"
                />

                {/* wishlist heart over image (optional) */}
                <button
                  className={`wishlist-toggle-btn ${isWished ? 'active' : ''}`}
                  onClick={(e) => {
                    e.stopPropagation();
                    onAddToWishlist(product);
                  }}
                  aria-label="Toggle wishlist"
                >
                  <i className="fas fa-heart" />
                </button>
              </div>

              <div className="shop-product-body">
                <span className="shop-product-weight">1kg</span>
                <p className="shop-product-category">
                  {product.category}
                </p>
                <h3
                  className="shop-product-name"
                  onClick={() => onOpenProduct(product)}
                >
                  {product.name}
                </h3>
                <p className="shop-product-price">₹{product.price}</p>

                <div className="shop-product-actions">
                  {qty === 0 ? (
                    <button
                      className="shop-btn shop-btn-primary"
                      onClick={() => onSetCartQuantity(product, 1)}
                    >
                      <i className="fas fa-shopping-cart" /> Add to Cart
                    </button>
                  ) : (
                    <div className="shop-quantity-control">
                      <button
                        className="qty-btn"
                        onClick={() =>
                          onSetCartQuantity(product, qty - 1)
                        }
                      >
                        −
                      </button>
                      <span className="qty-value">{qty}</span>
                      <button
                        className="qty-btn"
                        onClick={() =>
                          onSetCartQuantity(product, qty + 1)
                        }
                      >
                        +
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </article>
          );
        })}
      </div>
    </div>
  );
}

export default Shop;
