import React, { useState, useEffect, useRef } from 'react';
import './SearchBox.css';

const SearchBox = ({ products, onSearch, onSelectProduct }) => {
  const [searchInput, setSearchInput] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [searchResults, setSearchResults] = useState([]);
  const [hasSearched, setHasSearched] = useState(false);
  const searchRef = useRef(null);

  // Generate suggestions from products
  const generateSuggestions = (value) => {
    if (!value.trim()) {
      setSuggestions([]);
      return;
    }

    const lowerValue = value.toLowerCase();
    const filtered = products.filter((p) =>
      p.name.toLowerCase().includes(lowerValue) ||
      p.category?.toLowerCase().includes(lowerValue)
    );

    // Get unique suggestions (max 5)
    const uniqueSuggestions = [...new Set(filtered.map((p) => p.name))].slice(
      0,
      5
    );
    setSuggestions(uniqueSuggestions);
  };

  // Handle search input change
  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchInput(value);
    generateSuggestions(value);
    setShowSuggestions(true);
    setHasSearched(false);
  };

  // Handle suggestion click
  const handleSuggestionClick = (suggestion) => {
    setSearchInput(suggestion);
    setShowSuggestions(false);
    performSearch(suggestion);
  };

  // Perform actual search
  const performSearch = (searchTerm) => {
    const lowerSearchTerm = searchTerm.toLowerCase();
    const results = products.filter((p) =>
      p.name.toLowerCase().includes(lowerSearchTerm) ||
      p.category?.toLowerCase().includes(lowerSearchTerm)
    );

    setSearchResults(results);
    setHasSearched(true);
    onSearch(results);
  };

  // Handle search button click
  const handleSearchClick = () => {
    if (searchInput.trim()) {
      performSearch(searchInput);
      setShowSuggestions(false);
    }
  };

  // Handle keyboard Enter
  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearchClick();
    }
  };

  // Close suggestions when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (searchRef.current && !searchRef.current.contains(e.target)) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="search-box-container" ref={searchRef}>
      <div className="search-box-wrapper">
        <input
          type="text"
          className="search-box-input"
          placeholder="Search for fruits, vegetables, juices..."
          value={searchInput}
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          onFocus={() => searchInput && setShowSuggestions(true)}
        />
        <button className="search-box-btn" onClick={handleSearchClick}>
          <i className="fas fa-search"></i>
        </button>

        {/* Autocomplete Suggestions */}
        {showSuggestions && suggestions.length > 0 && (
          <div className="search-suggestions">
            {suggestions.map((suggestion, idx) => (
              <div
                key={idx}
                className="search-suggestion-item"
                onClick={() => handleSuggestionClick(suggestion)}
              >
                <i className="fas fa-search"></i>
                <span>{suggestion}</span>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Search Results */}
      {hasSearched && (
        <div className="search-results-container">
          {searchResults.length > 0 ? (
            <div className="search-results-grid">
              {searchResults.map((product) => (
                <article
                  key={product._id}
                  className="search-result-card"
                  onClick={() => onSelectProduct && onSelectProduct(product)}
                >
                  <div className="search-result-image">
                    <img
                      src={product.imageUrl}
                      alt={product.name}
                      className="search-result-img"
                    />
                  </div>
                  <div className="search-result-body">
                    <p className="search-result-category">
                      {product.category}
                    </p>
                    <h3 className="search-result-name">{product.name}</h3>
                    <p className="search-result-price">â‚¹{product.price}</p>
                  </div>
                </article>
              ))}
            </div>
          ) : (
            <div className="search-no-results">
              <i className="fas fa-search"></i>
              <h3>No Products Found</h3>
              <p>
                Sorry, we couldn't find any products matching "
                <strong>{searchInput}</strong>"
              </p>
              <button
                className="search-reset-btn"
                onClick={() => {
                  setSearchInput('');
                  setHasSearched(false);
                  setSearchResults([]);
                  onSearch([]);
                }}
              >
                Clear Search
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchBox;
