import React from 'react';
import { useParams } from 'react-router-dom';
import './ProductDetails.css';

const ProductDetails = ({ products, onAddToCart }) => {
  const { id } = useParams();
  const product = products.find((p) => p._id === id);

  if (!product) {
    return <div className="page-container">Product not found.</div>;
  }

  return (
    <div className="product-details-page">
      <div className="product-details-left">
        <img
          src={product.imageUrl}
          alt={product.name}
          className="product-details-main-img"
        />
      </div>

      <div className="product-details-right">
        <p className="breadcrumbs">Home / Shop / {product.name}</p>
        <h1 className="pd-title">{product.name}</h1>
        <p className="pd-category">{product.category}</p>
        <p className="pd-price">
          â‚¹{product.price}
        </p>

        <button
          className="shop-btn shop-btn-primary"
          onClick={() => onAddToCart(product)}
        >
          <i className="fas fa-shopping-cart" /> Add to cart
        </button>

        {/* add description, features etc here later */}
      </div>
    </div>
  );
};

export default ProductDetails;
