import React, { useState, useMemo, useEffect } from 'react';
import './Shop.css';

const PAGE_SIZE = 12;

const Shop = ({
  products,
  loadingProducts,
  cartItems,
  wishlistItems,
  onSetCartQuantity,
  onAddToWishlist,
  onOpenProduct,
}) => {
  const [activeCategory, setActiveCategory] = useState('all');
  const [page, setPage] = useState(1);
  const [sortBy, setSortBy] = useState('default');
  const [currentSlide, setCurrentSlide] = useState(0);

  const categories = [
    { key: 'vegetables', label: 'Vegetables', icon: 'ðŸ¥¬' },
    { key: 'fruits', label: 'Fruits', icon: 'ðŸŽ' },
  ];

  const sliderImages = [
    { src: '/banner1.jpg', alt: 'Fresh Vegetables' },
    { src: '/banner2.jpg', alt: 'Organic Fruits' },
    { src: '/banner3.jpg', alt: 'Healthy Produce' },
  ];

  useEffect(() => {
    const id = setInterval(() => {
      setCurrentSlide((prev) =>
        prev === sliderImages.length - 1 ? 0 : prev + 1
      );
    }, 5000);
    return () => clearInterval(id);
  }, [sliderImages.length]);

  const filteredProducts = useMemo(() => {
    if (activeCategory === 'all') return products;
    return products.filter(
      (p) => p.category?.toLowerCase() === activeCategory
    );
  }, [products, activeCategory]);

  const sortedProducts = useMemo(() => {
    const list = [...filteredProducts];
    if (sortBy === 'price-asc') {
      list.sort((a, b) => (a.price || 0) - (b.price || 0));
    } else if (sortBy === 'price-desc') {
      list.sort((a, b) => (b.price || 0) - (a.price || 0));
    } else if (sortBy === 'name-asc') {
      list.sort((a, b) =>
        (a.name || '').localeCompare(b.name || '', 'en', {
          sensitivity: 'base',
        })
      );
    }
    return list;
  }, [filteredProducts, sortBy]);

  const totalPages = Math.max(1, Math.ceil(sortedProducts.length / PAGE_SIZE));
  const currentPage = Math.min(page, totalPages);
  const startIdx = (currentPage - 1) * PAGE_SIZE;
  const pageItems = sortedProducts.slice(startIdx, startIdx + PAGE_SIZE);

  const handleCategoryClick = (key) => {
    setActiveCategory((prev) => (prev === key ? 'all' : key));
    setPage(1);
  };

  return (
    <div className="shop-page">
      {/* Hero Slider */}
      <section className="shop-hero">
        <div className="hero-slider">
          <div className="slider-container">
            <div
              className="slider-track"
              style={{
                transform: `translateX(-${currentSlide * 100}%)`,
              }}
            >
              {sliderImages.map((img, idx) => (
                <div key={idx} className="slider-slide">
                  <div className="slide-overlay"></div>
                  <img src={img.src} alt={img.alt} />
                  <div className="slide-content">
                    <h2>{img.alt}</h2>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <button
            className="slider-btn slider-prev"
            onClick={() =>
              setCurrentSlide((prev) =>
                prev === 0 ? sliderImages.length - 1 : prev - 1
              )
            }
          >
            â€¹
          </button>
          <button
            className="slider-btn slider-next"
            onClick={() =>
              setCurrentSlide((prev) =>
                prev === sliderImages.length - 1 ? 0 : prev + 1
              )
            }
          >
            â€º
          </button>

          <div className="slider-dots">
            {sliderImages.map((_, idx) => (
              <button
                key={idx}
                className={`dot ${idx === currentSlide ? 'active' : ''}`}
                onClick={() => setCurrentSlide(idx)}
              ></button>
            ))}
          </div>
        </div>
      </section>

      {/* Categories */}
      <section className="shop-categories">
        <div className="container">
          <h2>Shop by Category</h2>
          <div className="categories-grid">
            {categories.map((cat) => (
              <button
                key={cat.key}
                className={`category-card ${
                  activeCategory === cat.key ? 'active' : ''
                }`}
                onClick={() => handleCategoryClick(cat.key)}
              >
                <div className="category-icon">{cat.icon}</div>
                <span>{cat.label}</span>
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Toolbar */}
      <section className="shop-toolbar">
        <div className="container">
          <div className="toolbar-left">
            <h3>
              {activeCategory === 'all'
                ? 'All Products'
                : activeCategory === 'vegetables'
                ? 'Vegetables'
                : 'Fruits'}
            </h3>
            <p className="product-count">
              {pageItems.length} of {sortedProducts.length} items
            </p>
          </div>
          <div className="toolbar-right">
            <select
              className="sort-select"
              value={sortBy}
              onChange={(e) => {
                setSortBy(e.target.value);
                setPage(1);
              }}
            >
              <option value="default">Default</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
              <option value="name-asc">Name: A to Z</option>
            </select>
          </div>
        </div>
      </section>

      {/* Products Grid */}
      <section className="shop-products">
        <div className="container">
          {loadingProducts ? (
            <div className="loading">
              <div className="spinner"></div>
              <p>Loading products...</p>
            </div>
          ) : pageItems.length === 0 ? (
            <div className="empty-state">
              <p>No products available</p>
            </div>
          ) : (
            <div className="products-grid">
              {pageItems.map((product, idx) => {
                const qty =
                  cartItems?.find(
                    (item) => item.product._id === product._id
                  )?.quantity || 0;

                const isInWishlist = wishlistItems?.some(
                  (item) => item._id === product._id
                );

                return (
                  <div
                    key={product._id}
                    className="product-card"
                    style={{ animationDelay: `${idx * 0.05}s` }}
                  >
                    <div
                      className="product-image"
                      onClick={() => onOpenProduct(product)}
                    >
                      <img src={product.imageUrl} alt={product.name} />
                      <div className="image-overlay">
                        <button className="view-btn">View Details</button>
                      </div>
                    </div>

                    <div className="product-info">
                      <div className="product-header">
                        <h4 className="product-name">
                          {product.name}
                        </h4>
                        <button
                          className={`wishlist-btn ${
                            isInWishlist ? 'active' : ''
                          }`}
                          onClick={() => onAddToWishlist(product)}
                          title="Add to wishlist"
                        >
                          <i className="fas fa-heart"></i>
                        </button>
                      </div>

                      <p className="product-category">
                        {product.category}
                      </p>

                      <div className="product-price">
                        <span className="current-price">
                          â‚¹{product.price}
                        </span>
                        <span className="original-price">
                          â‚¹
                          {Math.round(product.price * 1.15)}
                        </span>
                      </div>

                      <div className="product-actions">
                        {qty === 0 ? (
                          <button
                            className="add-to-cart-btn"
                            onClick={() =>
                              onSetCartQuantity(product, 1)
                            }
                          >
                            <i className="fas fa-shopping-cart"></i>
                            Add
                          </button>
                        ) : (
                          <div className="qty-control">
                            <button
                              className="qty-btn"
                              onClick={() =>
                                onSetCartQuantity(product, qty - 1)
                              }
                            >
                              âˆ’
                            </button>
                            <span className="qty-value">
                              {qty}
                            </span>
                            <button
                              className="qty-btn"
                              onClick={() =>
                                onSetCartQuantity(product, qty + 1)
                              }
                            >
                              +
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </section>

      {/* Pagination */}
      {totalPages > 1 && (
        <section className="shop-pagination">
          <div className="container">
            <div className="pagination">
              <button
                className="page-btn"
                disabled={currentPage === 1}
                onClick={() => setPage((p) => Math.max(1, p - 1))}
              >
                <i className="fas fa-chevron-left"></i>
              </button>

              {Array.from({ length: totalPages }).map((_, idx) => {
                const pageNum = idx + 1;
                return (
                  <button
                    key={pageNum}
                    className={`page-btn ${
                      currentPage === pageNum ? 'active' : ''
                    }`}
                    onClick={() => setPage(pageNum)}
                  >
                    {pageNum}
                  </button>
                );
              })}

              <button
                className="page-btn"
                disabled={currentPage === totalPages}
                onClick={() =>
                  setPage((p) => Math.min(totalPages, p + 1))
                }
              >
                <i className="fas fa-chevron-right"></i>
              </button>
            </div>
          </div>
        </section>
      )}
    </div>
  );
};

export default Shop;
